#include <bits/stdc++.h>
using namespace std;
#define ull unsigned long long
#define d double

int main(){
    // freopen("ladder.in", "r", stdin);
    // freopen("ladder.out", "w", stdout);
    int n; string f, s;
    cin>>n;
    map<string, pair<int, int>>list;
    string names; pair<int, int>points;
    for(int i=0; i<n; i++){
        cin>>f>>points.first>>s>>points.second;
        bool found=true;
        names=f + " and " + s;
        for(auto it=list.begin(); it!=list.end(); it++){
            string tp = it->first;
            if(names == tp.substr(0, names.size()) && points!=(it->second)){
                if(names==tp) names+='1';
                else{
                    int next = stoi(tp.substr(names.size(), tp.size()-names.size())) + 1;
                    names += to_string(next);
                }
                
                list[names] = points;
                found=false;
                break;
            }
            else if(names == tp.substr(0, names.size()) && points!=(it->second)){
                found=false;
                break;
            }
        }
        if(found) list[names]=points;
    }
    for(auto it=list.begin(); it!=list.end(); it++){
        string name = it->first; pair<int, int> copy = it->second;
        for(int i=name.size()-1; i>=0; i--){
            if(!(int(name[i])>=48 && int(name[i])<=57)){
                name = name.substr(0, i+1);
                break;
            }
        }
        cout<<name<<" "<<copy.first+copy.second<<"\n";
    }

}