#include <bits/stdc++.h>
using namespace std;
#define pb push_back

vector<int>left_one(vector<int> d){
    vector<int>left(100002);stack<int>s;
    int n=d.size();
    for(int i=0; i<n; i++){
        while(!s.empty() && d[i]<=d[s.top()])s.pop();
        if(!s.empty())left[i]=s.top()+1;
        else left[i]=0;
        s.push(i); 
    }
    return left;
}
vector<int>right_one(vector<int> d){
    vector<int>right(100002);stack<int>s;
    int n=d.size();
    for(int i=n-1; i>=0; i--){
        while(!s.empty() && d[i]<=d[s.top()])s.pop();
        if(!s.empty())right[i]=s.top()-1;
        else right[i]=d.size()-1;
        s.push(i); 
    }
    return right;
}
int res(vector<int> d){
    vector<int>left = left_one(d); vector<int>right = right_one(d);
    int mx=d.size();
    // for(int i=0; i<d.size(); i++)cout<<left[i]<<" ";
    // cout<<"\n";
    // for(int i=0; i<d.size(); i++)cout<<right[i]<<" ";
    // cout<<"\n";
    for(int i=0; i<d.size(); i++){
        if(mx<d[i]*(right[i]-left[i]+1)) mx = d[i]*(right[i] - left[i]+1);
    }
    return mx;
}
int main(){
    // freopen("ladder.in", "r", stdin);
    // freopen("ladder.out", "w", stdout);
    int n;
    cin>>n;
    vector<int>d(n);
    for(int i=0; i<n; i++)cin>>d[i];
    cout<<res(d);
}